query SalarySearchFAQsQuery($cityId: Int, $metroId: Int, $stateId: Int, $countryId: Int, $jobTitle: String!, $hasJobTitle: Boolean!, $industryId: Int!, $domain: String, $locale: String, $gdId: String, $ip: String, $userId: Int, $enableV3Estimates: Boolean, $useUgcSearch2ForSalaries: String) {
  occMedianSalary(
    location: {cityId: $cityId, metroId: $metroId, stateId: $stateId, countryId: $countryId}
    jobTitle: {text: $jobTitle}
    industry: {id: $industryId}
    context: {domain: $domain, locale: $locale, gdId: $gdId, ip: $ip, userId: $userId, params: [{key: "useUgcSearch2", value: $useUgcSearch2ForSalaries}]}
    enableV3Estimates: $enableV3Estimates
  ) @include(if: $hasJobTitle) {
    confidence
    jobTitle {
      text
      __typename
    }
    nationalJobTitle {
      id
      text
      __typename
    }
    nationalSalariesCount
    nationalLow
    nationalMedian
    nationalHigh
    links {
      salarySearchUrl
      nationalSalarySearchUrl
      __typename
    }
    salariesCount
    low
    median
    high
    queryLocation {
      id
      name
      longName
      seoAlias
      __typename
    }
    currency {
      symbol
      code
      __typename
    }
    nationalCurrency {
      code
      symbol
      __typename
    }
    payPeriod
    __typename
  }
}

query SalariesFeaturedJobsQuery($keyword: String!, $locationType: LocationTypeEnum, $locationId: Int!, $adSlotName: String!, $numPerPage: Int!) {
  jobListings(
    contextHolder: {adSlotName: $adSlotName, searchParams: {numPerPage: $numPerPage, searchType: FJ, skipFeaturedJobs: true, skipUrgencyLabel: true, locationType: $locationType, locationId: $locationId, keyword: $keyword}}
  ) {
    jobListings {
      jobview {
        job {
          listingId
          __typename
        }
        overview {
          id
          name
          squareLogoUrl(size: SMALL)
          __typename
        }
        rating {
          starRating
          __typename
        }
        header {
          ageInDays
          jobLink
          jobTitleText
          locationName
          payCurrency
          payPeriod
          payPercentile10
          payPercentile90
          salarySource
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

mutation RecordPageViewMutation($metaData: [KeyValuePair]!, $pageIdent: String!) {
  recordPageView(pageIdent: $pageIdent, metaData: $metaData) {
    totalCount
    __typename
  }
}
